   * If a value is both a [List] and a [Map], it's serialized as a [List].
   */
  bool stringifyJsonValue(object) {
    if (object is num) {
      if (!object.isFinite) return false;
      _sink.write(numberToString(object));
      return true;
    } else if (identical(object, true)) {
      _sink.write('true');
      return true;
    } else if (identical(object, false)) {
      _sink.write('false');
       return true;
    } else if (object == null) {
      _sink.write('null');
      return true;
    } else if (object is String) {
      _sink.write('"');
      escape(object);
      _sink.write('"');
      return true;
    } else if (object is List) {
      checkCycle(object);
      List a = object;
      _sink.write('[');
      if (a.length > 0) {
        stringifyValue(a[0]);
        for (int i = 1; i < a.length; i++) {
          _sink.write(',');
          stringifyValue(a[i]);
        }
      }
      _sink.write(']');
      _removeSeen(object);
      return true;
    } else if (object is Map) {
      checkCycle(object);
      Map<String, Object> m = object;
      _sink.write('{');
      String separator = '"';
      m.forEach((String key, value) {
        _sink.write(separator);
        separator = ',"';
        escape(key);
        _sink.write('":');
        stringifyValue(value);
      });
      _sink.write('}');
      _removeSeen(object);
      return true;
    } else {
      return false;
    }
  }

  void _removeSeen(object) {
    assert(!_seen.isEmpty);
    assert(identical(_seen.last, object));
    _seen.removeLast();
  }
}

/**
 * A subclass of [_JsonStringifier] which indents the contents of [List] and
 * [Map] objects using the specified indent value.
 */
class _JsonStringifierPretty extends _JsonStringifier {
  final String _indent;

  int _indentLevel = 0;

  _JsonStringifierPretty(_sink, _toEncodable, this._indent)
      : super._(_sink, _toEncodable);

  void _write([String value = '']) {
    _sink.write(_indent * _indentLevel);
    _sink.write(value);
  }

  /**
   * Serializes a [num], [String], [bool], [Null], [List] or [Map] value.
   *
   * Returns true if the value is one of these types, and false if not.
   * If a value is both a [List] and a [Map], it's serialized as a [List].
   */
  bool stringifyJsonValue(final object) {
    if (object is List) {
      checkCycle(object);
      List a = object;
      if (a.isEmpty) {
        _sink.write('[]');
      } else {
        _sink.writeln('[');
        _indentLevel++;
        _write();
        stringifyValue(a[0]);
        for (int i = 1; i < a.length; i++) {
          _sink.writeln(',');
          _write();
          stringifyValue(a[i]);
        }
        _sink.writeln();
        _indentLevel--;
        _write(']');
      }
      _seen.remove(object);
      return true;
    } else if (object is Map) {
      checkCycle(object);
      Map<String, Object> m = object;
      if (m.isEmpty) {
        _sink.write('{}');
      } else {
        _sink.write('{');
        _sink.writeln();
        _indentLevel++;
        bool first = true;
        m.forEach((String key, Object value) {
          if (!first) {
            _sink.writeln(',');
          }
          _write('"');
          escape(key);
          _sink.write('": ');
          stringifyValue(value);
          first = false;
        });
        _sink.writeln();
        _indentLevel--;
        _write('}');
      }
      _seen.remove(object);
      return true;
    }
    return super.stringifyJsonValue(object);
  }
}
