  static const _INITIAL_BUFFER_SIZE = 1024;

  final _ChunkedConversionCallback<List<int>> _callback;
  // TODO(11971, floitsch): use Uint8List instead of normal lists.
  List<int> _buffer = new List<int>(_INITIAL_BUFFER_SIZE);
  int _bufferIndex = 0;

